select impressions.variant as variant, impressions.impressions, coalesce(clicks.clicks, 0) as clicks from (select logs.variant, count(*) as impressions from "rec_ab_logs" as "logs" where "logs"."created_at" between '2025-02-01 00:00:00' and '2025-02-07 23:59:59' and "logs"."placement" = 'home' group by "logs"."variant") as "impressions" left join (select clicks.variant, count(*) as clicks from "rec_clicks" as "clicks" where "clicks"."created_at" between '2025-02-01 00:00:00' and '2025-02-07 23:59:59' and "clicks"."placement" = 'home' group by "clicks"."variant") as "clicks" on "clicks"."variant" = "impressions"."variant" order by "impressions"."variant" asc
